from django.contrib import admin
from django.contrib.sites.models import Site



@admin.register(Site)
class SiteAdmin(admin.ModelAdmin):
    list_display = ('domain', 'name')
    search_fields = ('domain', 'name')


class AccountingAccountsAdmin(admin.ModelAdmin):
    model = AccountingAccounts
    list_display = ['name', 
            'get_name'
           , ]

    def get_name(self, obj):
        return obj.accountTypes.description
    get_name.admin_order_field  = 'description'  #Allows column order sorting
    get_name.short_description = 'Tipo de Cuenta'  #Renames column head

    #Filtering on side - for some reason, this works
    #list_filter = ['title', 'author__name']
admin.site.register(AccountingAccounts, AccountingAccountsAdmin)
